  	flex: 0,
  	justifyContent: 'flex-start',
  	display: 'flex',
  },
  textBubble: {
  	padding: 10,
  	backgroundColor: '#E6E6E6',
  	justifyContent: 'flex-start',
  	flex: 0,
  	display: 'flex',
  	textAlign: 'left',
  	borderRadius: 8,
  },
  usernameText:{
  	fontSize:9,
  	display: 'flex',
  	justifyContent: 'flex-start',
  },
  dadJokesLogo: {
    marginLeft: 50,
    marginRight: 50,
    marginTop: 20,
    width: '70vh'
  },
  bodyContainer: {
    backgroundColor: 'rgba(52, 52, 52, 0.0)',
    width: '70vh',
    height: '100vh',
    alignItems: 'center',
    display: 'flex',
    flexDirection: 'column',
    position: 'absolute'
  },
  dadJokeContainer: {
    display: 'flex',
    flexDirection: 'column',
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',

  },
  middleBG: {
    height: '100vh',
    alignSelf: 'center'
  },
  dadJokeTextRow: {
    flex: 2,
    alignItems: 'center',
    justifyContent: 'center',
    display: 'flex'
  },
  dadJokeText: {
    textAlign: 'center',
    fontSize: '3vh',
    alignSelf: 'center',
    position: 'absolute',
    width: '60vh'
  },
  jokeBoxBG: {
    marginLeft: 50,
    marginRight: 50,
    flex: 2,
    display: 'flex',
    width: '65vh',
    alignSelf: 'center',
  },
  gifFrame: {
    flex:2,
  },
  giphyLink: {
    fontSize:15,
    marginBottom: 50,
  },
  getDadJokeButton: {
    fontSize:25,
    height: 40,
    width: "73.375vh",
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    alignSelf: 'center',
    backgroundColor: '#F7931E',
    borderWidth: 0,
  },
  credits: {
    position: 'absolute',
    right: 15,
    fontSize: '1.5vw',
    textAlign: 'center',
    bottom: 0,
    color: '#E6E6E6'
  }
}

 class OtherUserText extends Component {
	constructor(props) {
	    super(props);
	    this.state = {
	    	username: '',
	    	message: '',
	    };

	}

	componentDidMount(){
		var {message, username, timestamp} = this.props

		if(username){
			this.setState({username: username})
		}

		if(message){
			this.setState({message: message})
		}

	}

	componentWillUnmount(){
	}


	render(){
		const {username, message} = this.state

		let {chatRoomData} = this.state

		return (
			<Row style={styles.userTextContainer}>
				<Container>
					<Row style={styles.usernameText}>
						{username}
					</Row>
					<Row style={styles.textBubble}>
						{message}
					</Row>
				</Container>
			</Row>
		);
	}
}

export default OtherUserText;
