import React, {Component } from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import { Row, Container } from 'react-bootstrap';

import {socket} from '../services/socket'



 class ChatRoom extends Component {
	constructor(props) {
	    super(props);
	    this.state = {
	    	message: ''
	    };

	}

	componentDidMount(){
		//Get game data from server
		socket.emit("EnterRoom", "Player Entered Room!!!")

	    //Retrieve game data (from GetGameData socket call)
	    socket.on("RetrieveChatRoomData", (chatRoomData) => {
	    	console.log(chatRoomData)
	    })

	}

	componentWillUnmount(){
		socket.off("RetrieveChatRoomData")
	}


	render(){

		return (
			<Container>
				<h1>Chat Room</h1>

  				<TextField 
	    			id="outlined-basic" 
	    			label="Enter Message" 
	    			variant="outlined"  
	    			value={this.state.message} 
	    			onChange={(event) => this.setMessage(event.target.value)}
	    		/>

			</Container>
		);
	}
}

export default ChatRoom;
