import React, {Component } from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import { Row, Container } from 'react-bootstrap';

import {socket} from '../services/socket'

import CurrentUserText from '../components/CurrentUserText'

let styles = {
	chatThread: {
		backgroundColor: 'yellow',
		flex: 0,
		justifyContent: 'center',
		display: 'flex',
	}

}

 class ChatRoom extends Component {
	constructor(props) {
	    super(props);
	    this.state = {
	    	currentUsername: "User1",
	    	currentUserID: 1,
	    	message: '',
	    	chatRoomData: [
	    	]
	    };

	}

	componentDidMount(){

		// localStorage.removeItem('userID')
		// localStorage.removeItem('username')

		let userIDVal = localStorage.getItem('userID')
		let usernameVal = localStorage.getItem('username')

		if(!userIDVal){

	      socket.on("SetUserData", userData => {

	        localStorage.setItem('userID', userData.userID)
	        localStorage.setItem('username', userData.username)
	        console.log(userData)

	        this.setState({currentUsername: userData.username, currentUserID: userData.userID})

	        socket.emit("UserEnteredRoom", userData)
	      });

	      socket.emit("CreateUserData")
	    } 
	    else {
	    this.setState({currentUsername: usernameVal, currentUserID: userIDVal})
	      socket.emit("UserEnteredRoom", {userID: userIDVal, username: usernameVal})
	    }

	    //Retrieve game data (from GetGameData socket call)
	    socket.on("RetrieveChatRoomData", (chatRoomData) => {
	    	this.setState({chatRoomData: chatRoomData})
	    })

	}

	componentWillUnmount(){
		// socket.emit("disconnect", {username: this.state.currentUsername, userID: this.state.currentUserID})
		socket.off("RetrieveChatRoomData")
		socket.off("SetUserData")
	}

	setMessage(message){
		this.setState({message: message})
	}

	sendMessageData(){
		var {message, currentUsername, currentUserID} = this.state

		if(message.length > 0){

			socket.emit("SendMessage", {message: message, username: currentUsername, userID: currentUserID, timeStamp: null})

			this.setState({message: ''})
		}
	}


	render(){

		let {chatRoomData} = this.state

		return (
			<Container>
				<h1>Chat Room</h1>

				<Container style={styles.chatThread}>
					{chatRoomData.map( (messageData, index) => 

		    			<CurrentUserText username={messageData.username} message={messageData.message}/>

		    		)}


	    		</Container>

	    		<TextField 
		    			id="outlined-basic" 
		    			label="Enter Message" 
		    			variant="outlined"  
		    			value={this.state.message} 
		    			onChange={(event) => this.setMessage(event.target.value)}
		    		/>

	    		<Button variant="contained" color="primary" onClick={() => this.sendMessageData()}>
			 		 Send Message
				</Button>

			</Container>
		);
	}
}

export default ChatRoom;
