    alignSelf: 'center',
  },
  gifFrame: {
    flex:2,
  },
  giphyLink: {
    fontSize:15,
    marginBottom: 50,
  },
  getDadJokeButton: {
    fontSize:25,
    height: 40,
    width: "73.375vh",
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    alignSelf: 'center',
    backgroundColor: '#F7931E',
    borderWidth: 0,
  },
  credits: {
    position: 'absolute',
    right: 15,
    fontSize: '1.5vw',
    textAlign: 'center',
    bottom: 0,
    color: '#E6E6E6'
  }
}

 class CurrentUserText extends Component {
	constructor(props) {
	    super(props);
	    this.state = {
	    	username: '',
	    	message: '',
	    };

	}

	componentDidMount(){
		var {message, username, timestamp} = this.props

		if(username){
			this.setState({username: username})
		}

		if(message){
			this.setState({message: message})
		}

	}

	componentWillUnmount(){
	}


	render(){
		const {username, message} = this.state

		let {chatRoomData} = this.state

		return (
			<Container style={styles.currentUserTextContainer}>
				<Container>
					{username}
				</Container>
				<Container style={styles.textBubble}>
					{message}
				</Container>

			</Container>
		);
	}
}

export default CurrentUserText;
