import React, {Component } from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import { Row, Container } from 'react-bootstrap';

import {socket} from '../services/socket'



 class ChatRoom extends Component {
	constructor(props) {
	    super(props);
	    this.state = {
	    	message: '',
	    	chatRoomData: [
	    		{
	    			username: 'User1',
	    			message: "Message 1",
	    			timeStamp: null
	    		},
	    		{
	    			username: 'User2',
	    			message: "Message 2",
	    			timeStamp: null
	    		}
	    	]
	    };

	}

	componentDidMount(){
		//Get game data from server
		socket.emit("EnterRoom", "Player Entered Room!!!")

	    //Retrieve game data (from GetGameData socket call)
	    socket.on("RetrieveChatRoomData", (chatRoomData) => {
	    	console.log(chatRoomData)
	    })

	}

	componentWillUnmount(){
		socket.off("RetrieveChatRoomData")
	}

	setMessage(message){
		this.setState({message: message})
	}

	sendMessage(){
		var {message} = this.state

		if(message.length > 0){

			socket.emit("SendMessage", message)

			this.setState({message: ''})
		}
	}


	render(){

		let {chatRoomData} = this.state

		return (
			<Container>
				<h1>Chat Room</h1>

				{chatRoomData.map( (messageData, index) => 

	    			<div>{messageData.username}: {messageData.message}</div>
	    		)}

  				<TextField 
	    			id="outlined-basic" 
	    			label="Enter Message" 
	    			variant="outlined"  
	    			value={this.state.message} 
	    			onChange={(event) => this.setMessage(event.target.value)}
	    		/>

	    		<Button variant="contained" color="primary" onClick={() => this.sendMessage()}>
			 		 Send Message
				</Button>

			</Container>
		);
	}
}

export default ChatRoom;
